import React , { useState } from 'react';
import { useForm } from "react-hook-form";
import { makeStyles, withTheme } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import FilledInput from '@material-ui/core/FilledInput';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import "./styles/Form.css";

const useStyles = makeStyles((theme) => ({
    root: {
    '& > *': {
        margin: theme.spacing(1),
        color: 'white',
        background: 'white',
        },
    },
    textField: {
            margin: theme.spacing(1),
            width: '28ch',
        },
    btn: {
        background: "blue",
        width: '29ch'
    },
}));

export default function ComposedTextField(props) {
    const [name, setName] = useState("");
    const [title, setTitle] = useState("");
    const [msg, setMsg] = useState("");
    const [tags, setTags] = useState("");
    const {pictureUpload, handleSubmit} = useForm();
    const classes = useStyles();

    const handleName= (event) => {
        setName(event.target.value);
        console.log(name);
    };

    const handleTitle = (event) => {
        setTitle(event.target.value);
        console.log(tit);
    };

    const handleMsg = (event) => {
        setMsg(event.target.value);
        console.log(name);
    };

    const handleTag = (event) => {
        setTags(event.target.value);
        console.log(name);
    };

    const onSubmit = (data) => {
        console.log(data);
    }

    return (
        <div className="form-container">
            <h1 className="header">Memory</h1>
            <form className={classes.root} noValidate autoComplete="off" onSubmit={handleSubmit(onSubmit)}>

                <button className='toggleBtn' onClick={props.toggle}>X</button>

                <FormControl variant="outlined">
                    <InputLabel htmlFor="component-outlined">Name</InputLabel>
                    <OutlinedInput id="component-outlined" value={name} onChange={handleName} label="name" />
                </FormControl>

                <FormControl variant="outlined">
                    <InputLabel htmlFor="component-outlined">Title</InputLabel>
                    <OutlinedInput id="component-outlined" value={title} onChange={handleTitle} label="title" />
                </FormControl>
                
                <TextField
                    className= {classes.textField}
                    id="outlined-multiline-static"
                    label="Message"
                    multiline
                    rows={4}
                    variant="outlined"
                    onChange={handleMsg}
                    value={msg}
                />

                <TextField
                    className= {classes.textField}
                    id="outlined-multiline-static"
                    label="Tags"
                    multiline
                    rows={2}
                    variant="outlined"
                    onChange={handleTag}
                    value={tags}
                />

                <div>
                    <input ref={pictureUpload} type="file" name="picture" />
                </div>
                

                <Button className= {classes.btn} variant="contained" color="primary">
                    Submit
                </Button>
            </form>
        </div>
    );
}
