import React, {useState} from 'react';
import { Container, AppBar, Typography, Grow, Grid, makeStyles, Paper, Button } from '@material-ui/core';
import './styles/Home.css';
import CardItem from "./CardsItem";
import Form from "./Form";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
    btn:{
        color: 'white',
        background: "black"
    }
}));

export default function Home() {
    
    const classes = useStyles();
    const [visible, setVisible] = useState(true);

    const handleClick = () => {
        setVisible(!visible);
    }
    
    return (
        <>
        <Container class="jumbotron">
            <AppBar position="static" color="inherit">
                <Typography  variant="h2" align="center">Memories</Typography>    
            </AppBar>
        </Container>
        
        {/*<div>
            {visible ? null : <Form toggle={handleClick}/>}
        </div>*/}
        
        {/*<Button className={classes.btn} onClick={handleClick}>Add Post</Button>*/}
        <Form/>
        
        <div className={classes.root}>
            <Grid container spacing={3}>
                <Grid item xs={6}>
                    <CardItem name='Beach' pic='https://www.tripsavvy.com/thmb/mTIFv3ZxmtIegfah4q4gSlmZITI=/1868x1401/smart/filters:no_upscale()/GettyImages-632172305-5927229e3df78cbe7ef5c846.jpg'/>
                    <CardItem name='Disney' pic='https://image.cnbcfm.com/api/v1/image/106613182-1601413553874-dis.jpg?v=1614204369'/>
                </Grid>
                <Grid item xs={6}>
                    <CardItem name='Food' pic='https://images.immediate.co.uk/production/volatile/sites/2/2017/12/xmas-Cover-17v5-54a9395.jpg?quality=90&resize=768%2C574'/>
                    <CardItem name='Concert' pic='https://blogmedia.evbstatic.com/wp-content/uploads/wpmulti/sites/8/shutterstock_199419065.jpg'/>
                </Grid>
            </Grid>
        </div>
        </>

    )
}
